import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

import VisitItem from '../components/VisitItem';

import { ReactComponent as Plus } from '../assets/plus.svg';

const RestaurantDetailPage = () => {

    let urlParams = useParams();
    let [restaurant, setRestaurant] = useState([]);
    let [visits, setVisits] = useState([]);

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api/restaurants/${urlParams['restaurantName']}/`).then((response) => {
            setRestaurant(response.data)
        });

        axios.get(`http://127.0.0.1:8000/api/visits/${urlParams['restaurantName']}/`).then((response) => {
            setVisits(response.data);
        })
    }, []);

  return (
    <div>
        <div>
            <h1 className='fw-bold'>{restaurant?.title}</h1>
        </div>

        <div className='w-100 d-flex flex-column justify-content-between align-items-end px-3'>
            <form className='d-flex flex-row align-items-center'>
                <div>
                    <div className='my-2'>
                        <p className='m-0 p-0'>Date</p>
                        <input type='date' />
                    </div>

                    <div className='my-2'>
                        <p className='m-0 p-0'>Money spent</p>
                        <input type='number' />
                    </div>
                </div>
                <div>
                    <div className='my-2'>
                        <p className='m-0 p-0'>Rating</p>
                        <select>
                            <option value={1}>1</option>
                            <option value={2}>2</option>
                            <option value={3}>3</option>
                            <option value={4}>4</option>
                            <option value={5}>5</option>
                        </select>
                    </div>

                    <textarea className='h-100'></textarea>
                </div>
            </form>

            <a className='text-dark fw-medium border border-success rounded-2 p-2 link-underline link-underline-opacity-0'>Add visit <Plus /></a>
        </div>
        
        <hr />
        <div className='w-100 d-flex flex-column'>
            {visits.map((visit, key) => (
                <VisitItem key={key} visit={visit} />
            ))}
        </div>
    </div>
  )
}

export default RestaurantDetailPage
