import React, {useEffect, useState} from 'react';
import axios from 'axios';

const Login = () => {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const submit = async e => {
        e.preventDefault();

        const user = {
            username: username,
            password: password
        };

        const {data} = await axios.post('http://localhost:8000/token/', user, {headers: {'Content-Type': 'application/json'}, withCredentials: false});
    
        localStorage.clear();
        localStorage.setItem('access_token', data.access);
        localStorage.setItem('refresh_token', data.refresh);

        axios.defaults.headers.common['Authorization'] = `Bearer ${data['access']}`;

        window.location.href = '/';
    }

  return (
    <form className='container d-flex flex-column justify-content-around align-items-center'>
        <h1>Login</h1>

        <div className='d-flex flex-column justify-content-start align-items-start'>
            <p>Username</p>
            <input type='text' className='form-control'/>
        </div>

        <div>
            <p>Password</p>
            <input type='password' className='form-control'/>
        </div>

        <div>
            <button className='form-control btn btn-primary'>Submit</button>
        </div>
    </form>
  )
}

export default Login
