import React, {useEffect, useState} from 'react';
import axios from 'axios';

import { ReactComponent as CloseButton } from '../assets/close.svg';

const UpdateRestaurantForm = ({restaurant, showForm}) => {
    
    let [restaurantName, setRestaurantName] = useState(restaurant?.title);
    let [restaurantAddress, setRestaurantAddress] = useState(restaurant?.address);

    useEffect(() => {
        setRestaurantName(restaurant?.title);
        setRestaurantAddress(restaurant?.address);
    }, [restaurant])

    const updateRestaurantData = async e => {
        e.preventDefault();

        const updatedData = {
            id: restaurant?.id,
            title: restaurantName,
            address: restaurantAddress,
            type_of_cuisine: restaurant?.type_of_cuisine,
        }

        axios.put(`http://127.0.0.1:8000/api/restaurants/${restaurant.title}/`, updatedData, {
            headers: {'Content-Type': 'application/json'},
            withCredentials: false}).then((response) => {
                window.location.reload();
        })
    }

  return (
    <div className={showForm === true ? 'container-fluid position-fixed top-0 start-0 m-0 p-0 bg-dark bg-opacity-75 d-flex justify-content-center align-items-center' : 'invisible opacity-0 position-absolute'} style={{height: "100vh"}}>
        <form className='col-6 bg-black p-5 rounded-3' onSubmit={updateRestaurantData}>
            <div>
                <a onClick={(e) => {showForm=false}}><CloseButton /></a>
            </div>

            <h1 className='text-white'>Update Restaurant Info</h1>

            <div className='my-3'>
                <p className='text-white m-0'>Restaurant Name</p>
                <input type='text' value={restaurantName} onChange={(e) => setRestaurantName(e.target.value)} />
            </div>

            <div className='my-3'>
                <p className='text-white m-0'>Restaurant Address</p>
                <input type='text' value={restaurantAddress} onChange={(e) => setRestaurantAddress(e.target.value)} />
            </div>

            <div>
                <button type='submit' className='form-control btn btn-success'>Update</button>
            </div>
        </form>
    </div>
  )
}

export default UpdateRestaurantForm
