import React, { useState } from 'react';

const UpdateRestaurantForm = ({restaurant}) => {

    let [restaurantName, setRestaurantName] = useState(restaurant.title);
    let [restaurantAddress, setRestaurantAddress] = useState(restaurant.address);

    const updateRestaurantData = async e => {
        e.preventDefault();

        const updatedData = {
            title: restaurantName,
            address: restaurantAddress
        }

        axios.put(`http://127.0.0.1:8000/api/restaurants/${restaurant.title}/`, updatedData, {
            headers: {'Content-Type': 'application/json'},
            withCredentials: false}).then((response) => {
                window.location.reload();
        })
    }

  return (
    <div className='container-fluid position-fixed top-0 left-0 bg-dark opacity-50'>
        <form>
            <h1>Update Restaurant Info</h1>

            <div>
                <p>Restaurant Name</p>
                <input type='text' value={restaurant.title} />
            </div>

            <div>
                <p>Restaurant Address</p>
                <input type='text' value={restaurant.address} />
            </div>

            <div>
                <button type='submit' className='form-control btn btn-success'>Update</button>
            </div>
        </form>
    </div>
  )
}

export default UpdateRestaurantForm
